const IteratorServices = {
  createIterator(iterable) {
    return this.createIteratorObj(iterable);
  },

  createBackgroundImageIterator(context, nameToCreate, iterable) {
    context[backgroundUrlItObj] = context.createIteratorObj(context.state.backgroundImageUrls)
  },

  createFontPairIterator(context) {
    this.fontPairItObj = this.createIteratorObj(this.state.fontPairings);
  },

  createQuoteIterator() {
    this.quoteItObj = this.createIteratorObj(this.state.quotes);
  },

  *createIteratorObj(arr) {
    for(let obj of arr) {
      yield obj;
    }
  }
}

export default IteratorServices;
