import React, { Component } from 'react'

const UserContext = React.createContext();

class UserContextManager extends Component {

  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      userName: '',
      password: '',
    }
  }

  render() {
    return (
      <UserContext.Provider>
        {this.props.children}
      </UserContext.Provider>
    )
  }
}

export { UserContext, UserContextManager };
