import React from 'react'
import '../css/quote.css';
import { QuoteContext } from '../contexts/QuoteContextManager'; 

export default class QuoteDisplay extends React.Component {

  constructor(props) {
    super(props);
  }

  componentDidMount() {

  }

  render() {
    return (
      <QuoteContext>
        {function quoteConsumer(context) {
          const dynamicBackgroundStyles = {
            backgroundSize: 'cover',
            backgroundPosition: 'center center',
            backgroundImage: `url(${context.state.backgroundImageUrl})`,
          }

          const dynamicBodyFont = {
            fontFamily: context.state.fontPair['body']
          }

          const dynamicAuthorFont = {
            fontFamily: context.state.fontPair['author']
          }

          return (
            <div className="quote-display"style={dynamicBackgroundStyles}>
              <div className="quote-backdrop">
                <div className="quote-body" style={dynamicBodyFont}>{context.state.currentQuote.body}</div>
                <div className="quote-author" style={dynamicAuthorFont}>{context.state.quote.author}</div>
              </div>
            </div>
          )
        }}
      </QuoteContext>
    )
  }
}
