import React from 'react';
import './App.css';
import Menu from './components/Menu';
import Quote from './components/Quote';
import QuoteNav from './components/QuoteNav';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      quotes: [{
        body: 'test quote',
        author: 'Ryan Cahela'
      }],
      backgroundImageUrl: '',
      fontFamily: 'Arial, Helvetica, sans-serif'
    }
  }

  componentDidMount() {
    fetch('https://api.unsplash.com/photos/random', {
      headers: {
        Authorization: 'Client-ID 637ad8107185907a6e559449be25e4c6fb9429f66f500149003592bbf8bf49ce'
      }
    })
    .then(res => res.json())
    .then(resJson => {
      console.log(resJson);
      this.setState({
        backgroundImageUrl: resJson.urls.small
      })
    });
  }

  render() {
    return (
      <div className="App">
        <Menu />
        <Quote 
          backgroundImageUrl={this.state.backgroundImageUrl} 
          fontFamily={this.state.fontFamily}
          quote={this.state.quotes[0]}/>
        <QuoteNav />
      </div>
    );
  }
}

export default App;
